import { useContext, useMemo, useState } from "react";
import { TodoContext } from "./App";
import TodoItem from "./TodoItem";
import "./TodoList.css";

 const TodoList = ({todo, onUpdate, onDelete}) => {
    const storeData = useContext(TodoContext);
    

    //Í≤ÄÏÉâÏñ¥ Ï≤òÎ¶¨Ìï† State Î≥ÄÏàò ÎßåÎì§Í∏∞
    const [search, setSearch] = useState("");
    const onChangeSearch = (e) => {
        setSearch(e.target.value);
    };
    //ÏûÖÎ†•Ìïú Í≤ÄÏÉâÏñ¥Ïóê Îî∞Îùº Ìï† Ïùº ÏïÑÏù¥ÌÖú ÌïÑÌÑ∞ÎßÅ(ÎåÄÏÜåÎ¨∏Ïûê Íµ¨Î≥ÑX)
    const getSearchResult = () => {
        return search === ""
            ? todo
            : todo.filter((it) => 
                it.content.toLowerCase().includes(search.toLowerCase()));
    };

    //Ìï† Ïùº Î∂ÑÏÑù Í∏∞Îä•
    const analyzeTodo = useMemo(() => {
        // console.log("analyzeTodo Ìï®Ïàò Ìò∏Ï∂ú");
        const totalCount = todo.length;
        const doneCount = todo.filter((it) => it.isDone).length;
        const notDoneCount = totalCount - doneCount;
        return {
            totalCount,
            doneCount,
            notDoneCount,
        };
    }, [todo]);
    //Ìï®Ïàò analyzeTodo Ìò∏Ï∂úÌïòÍ≥† Î∞òÌôò Í∞ùÏ≤¥Î•º Íµ¨Ï°∞ Î∂ÑÌï¥ Ìï†Îãπ
    //useMemoÎäî Ìï®ÏàòÍ∞Ä ÏïÑÎãå Í∞íÏùÑ Î∞òÌôò - analyzeTodo() -> analyzeTodo
    const {totalCount, doneCount, notDoneCount} = analyzeTodo;

     return (
        <div className="TodoList">
            <h4>Todo List üå±</h4>
            <div>
                <div>Ï¥ùÍ∞úÏàò: {totalCount}</div>
                <div>ÏôÑÎ£åÎêú Ìï† Ïùº: {doneCount}</div>
                <div>ÏïÑÏßÅ ÏôÑÎ£åÌïòÏßÄ Î™ªÌïú Ìï† Ïùº: {notDoneCount}</div>
            </div>
            <input 
                value={search}
                onChange={onChangeSearch}
                className="searchbar" 
                placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî" />
            <div className="list_wrapper">
                {getSearchResult().map((it) => (
                // mapÏùÑ Ïù¥Ïö©Ìï¥ HTML ÏöîÏÜå Î∞òÎ≥µÌïòÏó¨ Î†åÎçîÎßÅ
                // <div>{it.content}</div>
                // mapÏùÑ Ïù¥Ïö©Ìï¥ Ïª¥Ìè¨ÎÑåÌä∏ Î∞òÎ≥µ/ key ÏÑ§Ï†ï(Ïª¥Ìè¨ÎÑåÌä∏ Íµ¨Î∂Ñ)
                    <TodoItem key={it.id} {...it} 
                            onUpdate={onUpdate} onDelete={onDelete}/>
                ))}
            </div>
        </div>
     ) 
 };

 //TodoList Ïª¥Ìè¨ÎÑåÌä∏Ïóê Ï†ÑÎã¨ÌïòÎäî Props Î™®Îëê Ï†úÍ±∞Ìï®
 //-> Todo Í∞íÏù¥ undefinedÏù¥ÎØÄÎ°ú length ÌîÑÎ°úÌçºÌã∞Î°ú Ï†ëÍ∑ºÌïòÎ©¥ Ïò§Î•ò Î∞úÏÉù
 //-> todoÏùò Í∏∞Î≥∏Í∞íÏùÑ Îπà Î∞∞Ïó¥Î°ú ÌïòÎäî defaultProps ÏÑ§Ï†ï
 TodoList.defaultProps = {
    todo: [],
 };

 export default TodoList;
