import { useState } from "react";
import TodoItem from "./TodoItem";
import "./TodoList.css";

 const TodoList = ({todo, onUpdate, onDelete}) => {
    //Í≤ÄÏÉâÏñ¥ Ï≤òÎ¶¨Ìï† State Î≥ÄÏàò ÎßåÎì§Í∏∞
    const [search, setSearch] = useState("");
    const onChangeSearch = (e) => {
        setSearch(e.target.value);
    };
    //ÏûÖÎ†•Ìïú Í≤ÄÏÉâÏñ¥Ïóê Îî∞Îùº Ìï† Ïùº ÏïÑÏù¥ÌÖú ÌïÑÌÑ∞ÎßÅ(ÎåÄÏÜåÎ¨∏Ïûê Íµ¨Î≥ÑX)
    const getSearchResult = () => {
        return search === ""
            ? todo
            : todo.filter((it) => 
                it.content.toLowerCase().includes(search.toLowerCase()));
    };

    //Ìï† Ïùº Î∂ÑÏÑù Í∏∞Îä•
    const analyzeTodo = () => {
        const totalCount = todo.length;
        const doneCount = todo.filter((it) => it.isDone).length;
        const notDoneCount = totalCount - doneCount;
        return {
            totalCount,
            doneCount,
            notDoneCount,
        };
    };
    const {totalCount, doneCount, notDoneCount} = analyzeTodo

     return (
        <div className="TodoList">
            <h4>Todo List üå±</h4>
            <div>
                <div>Ï¥ùÍ∞úÏàò: {totalCount}</div>
            </div>
            <input 
                value={search}
                onChange={onChangeSearch}
                className="searchbar" 
                placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî" />
            <div className="list_wrapper">
                {getSearchResult().map((it) => (
                // mapÏùÑ Ïù¥Ïö©Ìï¥ HTML ÏöîÏÜå Î∞òÎ≥µÌïòÏó¨ Î†åÎçîÎßÅ
                // <div>{it.content}</div>
                // mapÏùÑ Ïù¥Ïö©Ìï¥ Ïª¥Ìè¨ÎÑåÌä∏ Î∞òÎ≥µ/ key ÏÑ§Ï†ï(Ïª¥Ìè¨ÎÑåÌä∏ Íµ¨Î∂Ñ)
                    <TodoItem key={it.id} {...it} 
                            onUpdate={onUpdate} onDelete={onDelete}/>
                ))}
            </div>
        </div>
     ) 
 };

 export default TodoList;
