import { useMemo, useState } from "react";
import "./TodoList.css";
import TodoItem from "./TodoItem";

const TodoList = ({todo, onUpdate, onDelete}) => {
    //Í≤ÄÏÉâÏñ¥Î•º ÏÉÅÌÉúÎ°ú Í¥ÄÎ¶¨
    const [search, setSearch] = useState("");
    //Í≤ÄÏÉâÏñ¥Í∞Ä Î≥ÄÍ≤ΩÎê† Îïå Ìò∏Ï∂úÎêòÎäî Ìï®Ïàò
    const onChangeSearch = (e) => {
        setSearch(e.target.value); //Í≤ÄÏÉâÏñ¥ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
    };
    //Í≤ÄÏÉâ Í≤∞Í≥º Î∞òÌôòÌïòÎäî Ìï®Ïàò
    const getSearchResult = () => {
        return search === "" ? todo : todo.filter((it) => it.content.toLowerCase().includes(search.toLowerCase()));
    }

    const analyzeTodo = useMemo(() => {
        const totalCount = todo.length;
        const doneCount = todo.filter((it) => it.isDone).length;
        const notDoneCount = totalCount - doneCount;
        return {
            totalCount,
            doneCount, 
            notDoneCount,
        };
    }, [todo]);

    const {totalCount, doneCount, notDoneCount} = analyzeTodo;

    TodoList.defaultProps = {
        todo
    }

    return (
        <div className="TodoList">
            <h4>Todo List üå±</h4>
            <div>
                <div>Ï¥ùÍ∞úÏàò: {totalCount}</div>
                <div>ÏôÑÎ£åÎêú Ìï† Ïùº: {doneCount}</div>
                <div>ÏïÑÏßÅ ÏôÑÎ£åÌïòÏßÄ Î™ªÌïú Ìï† Ïùº: {notDoneCount}</div>
            </div>
            <input className="searchbar" onChange={onChangeSearch} 
            placeholder="Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•ÌïòÏÑ∏Ïöî" />
            <div className="list_wrapper">
                {getSearchResult().map((it) => (
                    <TodoItem key={it.id} {...it} onUpdate={onUpdate} onDelete={onDelete} />
                    // mapÏùÑ Ïù¥Ïö©Ìï¥ Ïª¥Ìè¨ÎÑåÌä∏Î•º Î¶¨Ïä§Ìä∏ ÌòïÌÉúÎ°ú Î∞òÎ≥µÏ†ÅÏúºÎ°ú Î†åÎçîÎßÅÌïòÎ†§Î©¥
                    // Î∞òÎìúÏãú Î¶¨Ïä§Ìä∏ ÎÇ¥Ïùò Í≥†Ïú†Ìïú keyÎ•º PropsÎ°ú Ï†ÑÎã¨Ìï¥Ïïº Ìï®
                ))}
            </div>
        </div>
    ) 
};
export default TodoList;
